function rot13(str) { // LBH QVQ VG!
  let answer = "";
  const A_CODE = 'A'.charCodeAt(0);
  const Z_CODE = 'Z'.charCodeAt(0);
  const SHIFT = 13;
  str = str.toUpperCase();

  for (let i=0; i< str.length; i++) {
      if ((/[A-Z]/i).test(str[i])) {
        if(str.charCodeAt(i) < (A_CODE + SHIFT)) {
          answer += String.fromCharCode(
            (Z_CODE + (str.charCodeAt(i) - A_CODE) - SHIFT + 1)
          );
        } else {
          answer += String.fromCharCode((str.charCodeAt(i) - SHIFT));
        };
      } else {
        answer += str.charAt(i); 
      }; 
  };
  //console.log(str);
  console.log(answer);
  return answer;
};

