function checkCashRegister(price, cash, cid) {
  var change = [];
  var status = "INSUFFICIENT_FUNDS";
  let must_return = cash - price;
  let total_in_drawer = 0;
  let values = {
    "PENNY":    0.01,
    "NICKEL":   0.05,
    "DIME":     0.1,
    "QUARTER":  0.25,
    "ONE":      1,
    "FIVE":     5,
    "TEN":      10,
    "TWENTY":   20,
    "ONE HUNDRED": 100
  };
  let amount = 0;
  // get total cash in drawer
  total_in_drawer = parseFloat(cid.reduce((a, val) => a + val[1], 0).toFixed(2));

  if (must_return > total_in_drawer) return { 
    "status":"INSUFFICIENT_FUNDS", 
    "change":change 
  };

  if (must_return == total_in_drawer) return { 
      "status":"CLOSED", 
      "change":cid 
  };

  for ( var i = cid.length-1; i >= 0; i--) {
     if ( cid[i][1] == 0 ) continue;
     if (cid[i][1] <= must_return) {

       // put all cash with this currency unit to change 
       must_return = Math.round((must_return-cid[i][1])*100)/100;
       //console.log( must_return, cid[i][0],cid[i][1] );
       change.push([cid[i][0],cid[i][1]]);
     } else if( values[cid[i][0]] <= must_return ) {
       amount = values[cid[i][0]] * Math.floor(must_return/values[cid[i][0]]);
       change.push([cid[i][0],amount]);
       must_return = Math.round((must_return - amount)*100)/100;
       //console.log( must_return, cid[i][0],amount );
     };
     if (must_return == 0) break;
  };

  console.log(must_return);

  if ( must_return > 0) return {
      "status":"INSUFFICIENT_FUNDS", 
      "change": []   
  };

  //console.log( change );
  return {"status":"OPEN","change":change};
}

