function dropElements(arr, func) {
  // Drop them elements.
  let answer = [];
  let flag = false;
  for (let i in arr) {
    if (func(arr[i]) && flag==false) {
      answer.push(arr[i]);
      flag = true;
    } else {
      if (flag==true) {
        answer.push(arr[i]);
      }
    };
  }
  return answer;
}

dropElements([1, 2, 3], function(n) {return n < 3; });
